/**
 * @file voice-recognizer
 * @author v_zhaoxinggang
 * @date 2020-07-21
 */

/* globals Page, swan, getApp masterManager */

import {TIPS, BTN_TEXT, JUSTIFY_CONTENT, PERM_TIPS, RIPPLE_GIF, ERR_MSG_MAP} from './const';
import {isFullScreen, isIos} from '../../common/utils/index.js';

Component({
    externalClasses: [
        'smt-mark',
        'smt-voice-panel',
        'smt-button-voice',
        'smt-word-box'
    ],
    properties: {
        // 底部按钮颜色
        buttonColor: {
            type: String,
            value: '#007cfa'
        },

        // 是否开启长语音
        longSpeech: {
            type: Boolean,
            value: false
        },

        // 听音模式
        mode: {
            type: String,
            value: 'dnn'
        },

        // 语音识别所用的场景值
        context: {
            type: String,
            value: 'input'
        },

        // 语音文案
        defaultText: {
            type: String,
            value: TIPS.DEFAULT_TEXT
        },

        // 是否要展示语音面板
        showPanel: {
            type: Boolean,
            value: false,
            observer: function (newVal, oldVal) {
                newVal && this.triggerStart();
            }
        },

        // 是否有 tabbar
        hasTabBar: {
            type: Boolean,
            value: false
        }
    },
    data: {
        btnText: BTN_TEXT.END,
        rippleGif: RIPPLE_GIF,
        verticalAlign: JUSTIFY_CONTENT.NORMAL,
        isIos,
        content: '',
        eventType: '',
        showAnimate: 0,
        animateParam: {
            transformOrigin: '50% 50%',
            duration: 500,
            timingFunction: 'ease',
            delay: 0
        },
        animate: true,
        isFullScreen,
        /* eslint-disable max-len */
        iconVoice: 'data:image/png;base64,'
        /* eslint-enable max-len */
    },
    attached() {
        this.voiceRecognizer = swan.ai.getVoiceRecognizer();
        this.voiceRecognizer.stop();
        this.setData({
            msg: this.data.defaultText
        });
    },
    methods: {
        /**
         * 控制语音面板展示影藏
         */
        triggerStart() {
            this.setData({
                eventType: 'start'
            });

            this.data.hasTabBar && swan.hideTabBar({
                animation: true
            });
            this.startAnimation();
            this.start();
        },
        /**
         * 开始识别
         */
        start() {
            this.voiceRecognizer.onStart(() => {
                this.setData({
                    eventType: 'stop',
                    btnText: BTN_TEXT.END,
                    showAnimate: 0,
                    msg: this.data.defaultText
                });
            });

            this.voiceRecognizer.onRecognize(res => {
                let showAnimate = res.result ? 1 : 0;
                this.setData({
                    content: res.result,
                    showAnimate,
                    msg: ''
                }, () => {
                    let query = swan.createSelectorQuery();
                    query.in(this).select('.result-cont').boundingClientRect(res => {
                        if (res.height > 60 && JUSTIFY_CONTENT.MORE !== this.data.verticalAlign) {
                            this.setData({
                                verticalAlign: JUSTIFY_CONTENT.MORE,
                                delMargin: true
                            });
                        }
                    }).exec();
                });
            });

            this.voiceRecognizer.onFinish(res => {
                this.setData({
                    content: res.result
                });
                this.stop();
            });

            this.voiceRecognizer.onError(err => {
                if (+err.errorCode === 2004 || +err.errorCode === 2005 || +err.errorCode === 10003) {
                    this.setData({
                        showPanel: false
                    });
                    swan.showModal({
                        title: PERM_TIPS.TITLE,
                        content: PERM_TIPS.CONTENT,
                        showCancel: false,
                        confirmText: PERM_TIPS.BTN_TEXT
                    });
                }
                err.errorMsg = this.promptConversion(err.errorCode, err.errorMsg);
                this.setData({
                    msg: err.errorMsg,
                    showAnimate: 2
                });
                this.errInit();
                this.voiceRecognizer.stop();
            });
            let {mode, longSpeech, context} = this.data;

            this.voiceRecognizer.start({mode, longSpeech, context});
        },
        /**
         * 停止识别
         */
        stop() {
            let content = this.data.content;
            if (content) {
                setTimeout(() => {
                    this.triggerEvent('stop', {content});
                }, 300);
                this.finish();
            }
            else {
                this.setData({
                    msg: TIPS.RETRY,
                    btnText: BTN_TEXT.START,
                    eventType: 'start',
                    showAnimate: 2
                });
                this.voiceRecognizer.stop();
            }
        },
        /**
         * 完成识别
         */
        finish() {
            this.voiceRecognizer.stop();
            this.endAnimation();
        },
        cancel() {
            this.voiceRecognizer.cancel();
            this.endAnimation(() => {
                this.triggerEvent('cancelend');
            });
        },
        /**
         * 开始动画
         */
        startAnimation() {
            const animation = swan.createAnimation(this.data.animateParam);
            animation.translateY(0).step();
            this.setData({
                animationData: animation.export()
            });
        },
        /**
         * 结束动画
         */
        endAnimation(callback) {
            const animation = swan.createAnimation(this.data.animateParam);
            animation.translateY(290).step();
            this.setData({
                animationData: animation.export()
            }, () => {
                setTimeout(() => {
                    this.panelInit();
                    this.data.hasTabBar && swan.showTabBar({
                        animation: true
                    });
                    callback && callback();
                }, 200);
            });
        },
        /**
         * 初始化语音面板
         */
        panelInit() {
            this.setData({
                showPanel: false,
                showAnimate: 0,
                verticalAlign: JUSTIFY_CONTENT.NORMAL,
                content: '',
                msg: this.data.defaultText,
                btnText: BTN_TEXT.END,
                eventType: '',
                delMargin: false
            });
        },
        /**
         * 错误初始化
         */
        errInit() {
            this.setData({
                verticalAlign: JUSTIFY_CONTENT.NORMAL,
                content: '',
                btnText: BTN_TEXT.START,
                eventType: 'start',
                delMargin: false
            });
        },
        /**
         * 错误提示处理
         *
         * @param {Number} errCode 错误码
         * @param {String} errorMsg 错误信息
         */
        promptConversion(errCode, errorMsg) {
            return ERR_MSG_MAP[errCode] !== undefined ? ERR_MSG_MAP[errCode] : errorMsg;
        }
    }
});
